var resolveName = require('css-tree').property;
var handlers = {
    'font': require('csso/lib/replace/property/font'),
    'font-weight': require('csso/lib/replace/property/font-weight'),
    'background': require('csso/lib/replace/property/background'),
    'border': require('csso/lib/replace/property/border'),
    'outline': require('csso/lib/replace/property/border')
};

module.exports = function compressValue(node) {
    if (!this.declaration) {
        return;
    }

    var property = resolveName(this.declaration.property);

    if (handlers.hasOwnProperty(property.basename)) {
        handlers[property.basename](node);
    }
};
