'use strict';

const cssTree = require('css-tree');
const Atrule = require('csso/cjs/clean/Atrule.cjs');
const Comment = require('csso/cjs/clean/Comment.cjs');
const Declaration = require('csso/cjs/clean/Declaration.cjs');
const Raw = require('csso/cjs/clean/Raw.cjs');
const Rule = require('csso/cjs/clean/Rule.cjs');
const TypeSelector = require('csso/cjs/clean/TypeSelector.cjs');
const WhiteSpace = require('csso/cjs/clean/WhiteSpace.cjs');

const handlers = {
    Atrule,
    Comment,
    Declaration,
    Raw,
    Rule,
    TypeSelector,
    WhiteSpace
};

function clean(ast, options) {
    cssTree.walk(ast, {
        leave(node, item, list) {
            if (handlers.hasOwnProperty(node.type)) {
                handlers[node.type].call(this, node, item, list, options);
            }
        }
    });
}

module.exports = clean;
