'use strict';

const cssTree = require('css-tree');
const font = require('csso/cjs/replace/property/font.cjs');
const fontWeight = require('csso/cjs/replace/property/font-weight.cjs');
const background = require('csso/cjs/replace/property/background.cjs');
const border = require('csso/cjs/replace/property/border.cjs');

const handlers = {
    'font': font,
    'font-weight': fontWeight,
    'background': background,
    'border': border,
    'outline': border
};

function compressValue(node) {
    if (!this.declaration) {
        return;
    }

    const property = cssTree.property(this.declaration.property);

    if (handlers.hasOwnProperty(property.basename)) {
        handlers[property.basename](node);
    }
}

module.exports = compressValue;
