function merge() {
    var dest = {};

    for (var i = 0; i < arguments.length; i++) {
        var src = arguments[i];
        for (var key in src) {
            dest[key] = src[key];
        }
    }

    return dest;
}

module.exports = require('css-tree/lib/syntax/create').create(
    merge(
        require('css-tree/lib/syntax/config/lexer'),
        require('css-tree/lib/syntax/config/parser'),
        require('css-tree/lib/syntax/config/walker')
    )
);
module.exports.version = require('css-tree/package.json').version;
