// @flow
import type {BackendType, WorkerImpl} from '@parcel/workers/src/types';

export function detectBackend(): BackendType {
  // $FlowFixMe
  if (process.browser) return 'web';

  switch (process.env.PARCEL_WORKER_BACKEND) {
    case 'threads':
    case 'process':
      return process.env.PARCEL_WORKER_BACKEND;
  }

  try {
    require('worker_threads');
    return 'threads';
  } catch (err) {
    return 'process';
  }
}

export function getWorkerBackend(backend: BackendType): Class<WorkerImpl> {
  switch (backend) {
    case 'threads':
      return require('@parcel/workers/src/threads/ThreadsWorker').default;
    case 'process':
      return require('@parcel/workers/src/process/ProcessWorker').default;
    case 'web':
      return require('@parcel/workers/src/web/WebWorker').default;
    default:
      throw new Error(`Invalid backend: ${backend}`);
  }
}
